namespace Home\Controller;
use Think\Controller\RestController;

class UserController extends RestController
{
    //获取用户信息
    public function get_info(){
        $token = I('token');
        if($token==false){
            $data['code'] = '2';
            $data['msg'] = '请先登录';
            $this->response($data,'json');
        }
        $user = M('user')->field('account,avatar,age,sex,email,status,logo_time')->where(array('token'=>$token))->find();
        if($user['account']){
            $data['code'] = '1';
            $data['msg'] = 'success';
            $data['result'] = $user;
        }else{
            $data['code'] = '0';
            $data['msg'] = '该用户不存在';
            $data['result'] = $user;
        }
        $this->response($data,'json');
    }

    //找回密码
    public function get_password(){
        $User = M("User");
        $account = I('account');
        $new_password = I("password");
        $new_password_md5 = md5($new_password);
        $old_password = $User->field("password") ->where(array('account'=>$account))->find();
        if($old_password==false){
            $response['code'] = '2';
            $response['msg'] = '该账号不存在';
            $this->response($response,'json');
        }
        if($new_password_md5==$old_password["password"]){
            $response['code'] = '3';
            $response['msg'] = '新密码不能与旧密码一致';
            $this->response($response,'json');
        }
        $data['password'] = $new_password_md5;
        $result =  $User->where(array('account'=>$account))->save($data);
        if($result == false){
            $response['code'] = '0';
            $response['msg'] = 'fail';
        }else{
            $response['code'] = '1';
            $response['msg'] = 'success';
        }
        $this->response($response,'json');
    }

    //获取用户收藏列表
    public function get_collect(){
        $User_collect = M("user_collect");
        if(I("m_type")){
            $where["music_type"] = I("m_type");
        }
        $where["user_token"]=I("token");
        $current_page = I("page");
        $list_rows = I("list_rows");
        $collect = $User_collect->alias("uc")
            ->join("category as c on uc.music_type=c.ca_id")
            ->join("left join music as m on uc.music_id=m.m_id")
            ->field("uc.id,uc.music_id,uc.music_type,uc.status,c.ca_type,m.m_name,m.m_avatar")
            ->where($where)
            ->page($current_page.','.$list_rows)
            ->select();

        if(count($collect)){
            $list["code"] = 1;
            $list["msg"] = "success";
            $list["num"] = count($collect);
            $list["detail"] = $collect;
        }else{
            $list["code"] = 0;
            $list["msg"] = "无数据";
        }
        $this->response($list,'json');
    }

    //不具备分页功能的收藏列表返回
    public function collect_list(){
        $User_collect = M("user_collect");
        $where["user_token"]=I("token");
        if(I("m_type")){
            $where["music_type"] = I("m_type");
        }
        $collect = $User_collect->alias("uc")
            ->join("category as c on uc.music_type=c.ca_id")
            ->field("uc.id,uc.music_id,uc.music_type,uc.status,c.ca_type")
            ->where($where)
            ->select();
        if(count($collect)){
            $list["code"] = 1;
            $list["msg"] = "success";
            $list["num"] = count($collect);
            $list["detail"] = $collect;
        }else{
            $list["code"] = 0;
            $list["msg"] = "fail";
        }
        $this->response($list,'json');
    }

    //用户的练习记录所有信息
    public function get_practice(){
        $User_collect = M("user_practice");
        //当前页
        $user_token = I("token");
        $current_page =I("page");
        $list_rows = I("list_rows");
        //进行分页数据查询
        $list = $User_collect->field('music_id,music.m_name,rate,last_time')
            ->join("music on music.m_id=user_practice.music_id")
            ->where(array('user_practice.user_token'=>$user_token))
            ->select();
        for($i=0;$i<count($list);$i++){
            $list[$i]["last_time"]=date('Y-m-d',$list['last_time']);
        }
        if(count($list)){
            $detail["code"] = 1;
            $detail["msg"] = "success";
            $detail["num"] = count($list);
            $detail["list"] = $list;
        }else{
            $detail["code"] = 0;
            $detail["msg"] = "fail";
        }
        $this->response($detail,'json');
    }

    //编辑用户头像
    public function avatar(){
        $where["token"] = I("token");
        $base64 = I("photo");
//        $base64 = "data:image/jpg;base64,";
        $lookUser = M("user")->field("account")->where($where)->find();
        if($lookUser==false){
            $detail["code"] = 2;
            $detail["msg"] = "此用户不存在";
            $this->response($detail,"json");
        }
        $base64_body = substr(strstr($base64,','),1);       //获取base64图片内容部分
        $img  = base64_decode($base64_body);                //解析base64
        $path = ROOT_PATH."/Uploads/avatar/";
        if(!file_exists($path)){                            //创建
            mkdir($path,0777,true);
        }
        //获取图片格式
        $sit = stripos($base64,"image/")+6;
        $photoType = substr($base64,$sit,3);

        file_put_contents($path.time().".".$photoType, $img);
        $data["avatar"] = "avatar/".time().".".$photoType;
        $saveData = M("user")->where($where)->data($data)->save();

        if($saveData){
            $detail["code"]=1;
            $detail["msg"] = "success";
            $detail["url"] = $data["avatar"];
        }else{
            $detail["code"]=0;
            $detail["msg"] = "fail";
        }
        $this->response($detail,"json");
    }

    //用户编辑个人信息
    public function change_info(){
        $where["token"] = I("token");
        $lookUser = M("user")->field("account")->where($where)->find();
        if($lookUser==false){
            $detail["code"] = 2;
            $detail["msg"] = "此用户不存在";
            $this->response($detail,"json");
        }
        $data["name"] = I("name");
        $data["age"] = I("age");
        $data["sex"] = I("sex");
        $data["phone"] = I("phone");
        $data['email'] = I("email");
        $data["logo_time"] = time();
        $saveInfo = M("user")->where($where)->data($data)->save();
        if($saveInfo){
            $detail["code"] = 1;
            $detail["msg"] = "success";
        }else{
            $detail["code"] = 0;
            $detail["msg"] = "fail";
        }
        $this->response($detail,"json");
    }
    //用户信息反馈
    public function feedback(){
        $data["token"] = I("token");
        $data["content"] = I("text");
        $data["create_time"] = time();
        $where["token"] = $data["token"];
        $user = M("user")->field("id")->where($where)->find();
        if($user["id"]==null){
            $detail["code"] = 2;
            $detail["msg"] = "该用户不存在";
            $this->response($detail,"json");
        }
        $resAdd = M("feedback")->data($data)->add();
        if($resAdd){
            $detail["code"] = 1;
            $detail["msg"] = "success";
        }else{
            $detail["code"] = 0;
            $detail["msg"] = "fail";
        }
        $this->response($detail,"json");
    }


}

